import{homeView}from"./views/home.min.js";
import{projetosView}from"./views/projetos.min.js";
import{cadastroView}from"./views/cadastro.min.js";
import{inicializarValidacao}from"./validation.min.js";
const rootElement=document.getElementById("app-root"),
routes={"/":homeView,"/projetos":projetosView,"/cadastro":cadastroView};
export const handleRoute=()=>
    {const o=window.location.hash.substring(1)||"/",[t,e]=o.split("#"),i=routes[t]||"<h2>Página não encontrada</h2>";
        if(rootElement.innerHTML=i,e){const o=document.getElementById(e);
            o&&setTimeout(()=>{o.scrollIntoView({behavior:"smooth"})},0)}"/cadastro"===t&&inicializarValidacao()};