const toggleButton=document.getElementById("theme-toggle"),htmlElement=document.documentElement,applyTheme=e=>{htmlElement.setAttribute("data-theme",e),localStorage.setItem("theme",e)},savedTheme=localStorage.getItem("theme")||"light";applyTheme(savedTheme);export const initThemeToggle=()=>{toggleButton&&toggleButton.addEventListener("click",()=>{const e=htmlElement.getAttribute("data-theme"),t="light"===e?"dark":"light";applyTheme(t)})};