function mostrarErro(r,e){const t=r.parentElement,o=t.querySelector(".form-error");o.textContent=e,r.classList.add("input-error")}function limparErros(){document.querySelectorAll(".form-error").forEach(r=>r.textContent=""),document.querySelectorAll(".input-error").forEach(r=>r.classList.remove("input-error")),document.getElementById("form-status-alert").innerHTML=""}function mostrarAlertaGeral(r,e){const t=document.getElementById("form-status-alert"),o=r?"alert-sucesso":"alert-erro";t.innerHTML=`<div class="alert ${o}">${e}</div>`}function validarEmail(r){const e=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return e.test(String(r).toLowerCase())}export const inicializarValidacao=()=>{const r=document.getElementById("form-de-cadastro");r&&r.addEventListener("submit",e=>{e.preventDefault(),limparErros();let t=!0;const o=document.getElementById("nome");o.value.trim().length<3&&(mostrarErro(o,"O nome deve ter pelo menos 3 caracteres."),t=!1);const a=document.getElementById("email");validarEmail(a.value)||(mostrarErro(a,"Por favor, insira um e-mail válido."),t=!1),t?(mostrarAlertaGeral(!0,"Cadastro enviado com sucesso! Entraremos em contato."),r.reset()):mostrarAlertaGeral(!1,"Por favor, corrija os erros no formulário.")})};